mapper.insert:
INSERT INTO
topic(
pub_name,
topic_name
)
VALUES (
#{pubName},
#{topicName}
)


  SELECT CAST(nextval('message_id_seq') as INTEGER)


INSERT INTO
message(
msg_id,
topic_name,
msg_content
)
VALUES (
#{msgId},
#{topicName},
#{msgContent}
);


INSERT INTO
subscriber_msg (
sub_id,
msg_id)
VALUES(
SELECT
sub_id,
#{msgId}
FROM
subscriber_topic
WHERE
topic_name=#{topicName}
)
;
