mapper.insert:
INSERT INTO
topic(
pub_id,
topic_name
)
VALUES (
#{pubId},
#{topicName}
)


  SELECT CAST(nextval('message_id_seq') as INTEGER)


INSERT INTO
message(
msg_id,
topic_id,
msg_content
)
VALUES (
#{msgId},
#{topicId},
#{msgContent}
);


INSERT INTO
subscriber_msg (
sub_id,
msg_id)
VALUES(
SELECT
sub_id,
#{msgId}
FROM
subscriber_topic
WHERE
topic_id=#{topicId}
)
;
